  constructor(private http: HttpClient) {

  }

  getWeatherForecast() {
    this.http.get('/api/weatherforecast')
      .pipe(
        tap((response) => {
          this.forecastSubject.next(response);
        }),
        catchError((e) => {
          if(e.status == 401) {
            this.forecastSubject.next({ 'error': 'You are not logged in' });
          }

          throw e;
        })
      ).subscribe();
  }

